function dispStringInCenter(display,dispString,vLoc,textSize,colorRgb,ShiftStim, MovingFixation)% dispStringInCenter(display,dispString,[vLoc],[textSize],[colorRgb]);%% 06/2005 SOD ported to OSXif ~exist('vLoc')    vLoc = 0.5;endif ~exist('textSize') | isempty(textSize),    textSize = 20;endif ~exist('colorRgb')    colorRgb = display.textColorRgb;enddisp(sprintf('[%s]:%s',mfilename,dispString));if(isfield(display, 'windowPtr'))    oldTextSize = Screen('TextSize', display.windowPtr, textSize);    charWidth = textSize/4.5;        if exist('ShiftStim','var')        if exist('MovingFixation','var')            drawFixation(display,1,1,MovingFixation.Center,MovingFixation.Type,MovingFixation.Radius,MovingFixation.Array,MovingFixation.ArraySize,ShiftStim);        else            drawFixation(display,1,1,[],[],[],[],[],ShiftStim);        end    else        drawFixation(display);    end        if (display.stereoFlag==0)        loc(1) = display.rect(3)/2-charWidth*length(dispString);        loc(2) = display.rect(4)*vLoc;        if exist('ShiftStim','var')            loc(1)=loc(1)+ShiftStim(1);            loc(2)=loc(2)+ShiftStim(2);        end        Screen('DrawText', display.windowPtr, dispString, loc(1), loc(2), display.textColorRgb);    else        loc(1) = display.rect(3)/4-charWidth*length(dispString);        loc(2) = display.rect(4)*vLoc;        loc(3) = 3*display.rect(3)/4-charWidth*length(dispString);        Screen('DrawText', display.windowPtr, dispString, loc(1), loc(2), display.textColorRgb);        Screen('DrawText', display.windowPtr, dispString, loc(3), loc(2), display.textColorRgb);    end    Screen('Flip',display.windowPtr);    Screen('TextSize', display.windowPtr, oldTextSize);endreturn